/*
 */

// @mui material components
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";

// Material Kit 2 React components
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";

// Material Kit 2 React examples
import DefaultInfoCard from "examples/Cards/InfoCards/DefaultInfoCard";
import { useState } from "react";
import {
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Paper,
  Typography,
  Divider,
} from "@mui/material";
import InboxIcon from "@mui/icons-material/Inbox";
import MailIcon from "@mui/icons-material/Mail";
import DraftsIcon from "@mui/icons-material/Drafts";
import bgImage from "assets/images/illustrations/contacto.jpg"

const menuItems = [
  {
    icon: <InboxIcon />,
    text: "Inbox",
    description: "Descripción del Inbox",
  },
  {
    icon: <MailIcon />,
    text: "Mail",
    description: "Descripción del Mail",
  },
  {
    icon: <DraftsIcon />,
    text: "Drafts",
    description: "Descripción del Drafts",
  },
];
const blueColor = "f0f0f0"; // Color azul

function Information() {
  const [selectedItem, setSelectedItem] = useState(menuItems[0]);

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };
  return (
    <MKBox component="section" pb={12}>
      <Container>
        <Grid container spacing={3} alignItems="center">
          <Grid item xs={12} lg={12}>
            <Grid container justifyContent="flex-start">
              <Grid item xs={12} md={6}>
                <MKBox mb={5}>
                  <DefaultInfoCard
                    icon="public"
                    title="EXÁMENES MÉDICO OCUPACIONALES"
                    description="Exámenes de ingresos periódicos y de egresos en empresas."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={6}>
                <MKBox mb={5}>
                  <DefaultInfoCard
                    icon="apps"
                    title="POLÍTICA DE CALIDAD"
                    description="SMLES proporciona servicios de Salud Laboral, para ayudar a las organizaciones a cumplir con la normativa vigente en materia de Salud Ocupacional, a través de la administración del médico en tú empresa, desarrollo tecnológico con enfoque a mejorar y seguir un programa de salud ocupacional."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={6}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="payments"
                    title="EXAMENES"
                    description="Visiometría ocupacional.
    Audiometría ocupacional.Espirometría ocupacional.Electrocardiograma.Psicología ocupacional.Laboratorio de análisis clínicos.Rayos X Ultrasonido "
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={6}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="3p"
                    title="EXPEDIENTES"
                    description="Cada uno de los expedientes está controlado y administrado por profesionales."
                  />
                </MKBox>{" "}
              </Grid>{" "}
            </Grid>{" "}
          </Grid>{" "}
          <Divider
            style={{
              backgroundColor: blueColor,
              height: 5,
              marginTop: 50,
              marginBottom: 100,
            }}
          />
          <Grid
            item
            xs={12}
            sx={{
              mb: 10,
            }}
          >
            <MKTypography
              variant="h2"
              color="info"
              sx={{
                textAlign: "center",
                mb: 3,
              }}
            >
              Marco legal y reglamento{" "}
            </MKTypography>{" "}
            <MKTypography
              variant="body1"
              color="dark"
              opacity={0.8}
              sx={{
                textAlign: "center",
              }}
            >
              Para más información sobre las disposiciones oficiales, dejamos a
              su alcance los siguientes documentos.{" "}
            </MKTypography>{" "}
          </Grid>{" "}
          <MKBox
            minHeight="25rem"
            opacity={0.8}
            width="100%"
            sx={{
              backgroundImage: `url(${bgImage})`,
              backgroundSize: "cover",
              backgroundPosition: "top",
              backgroundAttachment: "fixed", // Fondo estático
              display: "grid",
              placeItems: "center",
            }}
          >
            <Container>
              <Grid container>
                <Grid item md={12} xs={12}>
                  <Card variant="outlined">
                    <Grid
                      container
                      spacing={5}
                      className="side-by-side-boxes"
                      position="relative"
                    >
                      <Grid item xs={12} md={7}>
                        <MKBox>
                          <MKTypography
                            variant="h5"
                            color="gray"
                            p={5}
                            sx={{
                              lineHeight: 2,
                              fontStyle: "oblique",
                              textAlign: "justify",
                            }}
                          >
                            <strong>
                              Nuestra metodología se basa en tres pilares
                              fundamentales:
                            </strong>{" "}
                            el trabajo en equipo, la mejora continua y la
                            medición de servicios.Estos principios nos permiten
                            colaborar eficazmente con tu organización para
                            garantizar el cumplimiento de la normativa oficial
                            mexicana en el ámbito de la salud ocupacional.{" "}
                          </MKTypography>{" "}
                        </MKBox>{" "}
                      </Grid>{" "}
                      <Grid
                        item
                        xs={12}
                        md={5}
                        container
                        alignItems="center"
                        justify="center"
                      >
                        <MKBox>
                          <MKBox
                            component="img"
                            src={macbook}
                            alt="SMELS"
                            width="95%"
                          />
                        </MKBox>{" "}
                      </Grid>{" "}
                    </Grid>{" "}
                  </Card>{" "}
                </Grid>{" "}
              </Grid>
            </Container>{" "}
          </MKBox>{" "}
          <Grid item xs={12} lg={12} mt={12}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
              }}
            >
              <div
                style={{
                  flex: 1,
                }}
              >
                <Paper
                  elevation={3}
                  style={{
                    height: "100%",
                    overflow: "auto",
                  }}
                >
                  <List>
                    {" "}
                    {menuItems.map((item) => (
                      <ListItem
                        button
                        selected={item === selectedItem}
                        onClick={() => handleItemClick(item)}
                      >
                        <ListItemIcon> {item.icon} </ListItemIcon>{" "}
                        <ListItemText primary={item.text} />{" "}
                      </ListItem>
                    ))}{" "}
                  </List>{" "}
                </Paper>{" "}
              </div>{" "}
              <div
                style={{
                  flex: 2,
                  padding: "16px",
                }}
              >
                <Typography variant="h6"> {selectedItem.text} </Typography>{" "}
                <Typography> {selectedItem.description} </Typography>{" "}
              </div>{" "}
            </div>{" "}
          </Grid>{" "}
        </Grid>{" "}
      </Container>{" "}
      <Divider
        style={{
          backgroundColor: blueColor,
          height: 5,
          marginTop: 50,
          marginBottom: 100,
        }}
      />
    </MKBox>
  );
}

export default Information;
