/*
 */

// @mui material components
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";

// Material Kit 2 React components
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";

// Material Kit 2 React examples
import DefaultInfoCard from "examples/Cards/InfoCards/DefaultInfoCard";
import { useState } from "react";
import {
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Paper,
  Typography,
  Divider,
  Card
} from "@mui/material";
import InboxIcon from "@mui/icons-material/Inbox";
import MailIcon from "@mui/icons-material/Mail";
import DraftsIcon from "@mui/icons-material/Drafts";
import bgImage from "assets/images/v870-tang-35.jpg";

const menuItems = [
  {
    icon: <InboxIcon />,
    text: "Atención médica primaria",
    description: <MKTypography>Conocimiento mutuo sobre el tema</MKTypography>,
  },
  {
    icon: <MailIcon />,
    text: "Llenado de ST7 ",
    description: "Descripción del Mail",
  },
  {
    icon: <DraftsIcon />,
    text: "Atención de Accidentes laborales leves",
    description: "Descripción del Drafts",
  },
  {
    icon: <DraftsIcon />,
    text: "Exámenes Médicos",
    description: "Descripción del Drafts",
  },
  {
    icon: <DraftsIcon />,
    text: "Campañas de salud ",
    description: "Descripción del Drafts",
  },
  {
    icon: <DraftsIcon />,
    text: "Campañas y platicas  de Seguridad",
    description: "Descripción del Drafts",
  },
];

const blueColor = "f0f0f0"; // Color azul

function Information() {
  const [selectedItem, setSelectedItem] = useState(menuItems[0]);

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };
  return (
    <MKBox component="section" pb={12}>
        <Grid container spacing={5} alignItems="center">
          <Grid item sm={12} lg={12} mb={12}>
          <Container>
          <Grid container justifyContent="flex-start">
              <Grid item xs={12} md={4} my={3}>
                <MKBox mb={5}>
                  <DefaultInfoCard
                    icon="inventory"
                    title="Servicios Médicos Profesionales"
                    description="Servicio bajo coordinación de un médico con conocimiento y experiencia en salud laboral."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox mb={5}>
                  <DefaultInfoCard
                    icon="medical_services"
                    title="Salud En Tu Empresa"
                    description="Programa de salud en tu empresa estandarizado."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="badge"
                    title="Manejo y Contrato De Médicos Calificados"
