/*
=========================================================
* Material Kit 2 React - v2.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/material-kit-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

Coded by www.creative-tim.com

 =========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

// @mui material components
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";

// Material Kit 2 React components
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
/* import MKInput from "components/MKInput";
import MKButton from "components/MKButton"; */

// Images
import macbook from "assets/images/logos/gray-logos/logols2.png";
import bgImage from "assets/images/bgus.jpg";
import { Card } from "@mui/material";

function Newsletter() {
  return (

    <MKBox
      minHeight="25rem"
      opacity={0.8}
      width="100%"
      sx={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: "cover",
        backgroundPosition: "top",
        backgroundAttachment: "fixed", // Fondo estático
        display: "grid",
        placeItems: "center",
      }}
    >
                 <MKTypography
              variant="h2"
              color="dark"
              sx={{
                textAlign: "center",
                mb: 3,
              }}
            >
              ¡Estamos a tu servicio!{" "}
            </MKTypography>{" "}
            <MKTypography
              variant="body1"
              color="dark"
              opacity={0.8}
              sx={{
                textAlign: "center",
              }}
            >
              Con más de 10 años de experiencia, ¡Estamos listos para ayudarte!
            </MKTypography>{" "}
      <Container>

      <Grid container>
  <Grid item md={12} xs={12}>
    <Card variant="outlined">
      <Grid container spacing={5} className="side-by-side-boxes" position="relative">
        <Grid item xs={12} md={7}>
          <MKBox>
            <MKTypography
              variant="h5"
              color="gray"
              p={5}
              sx={{
                lineHeight: 2,
                fontStyle: "oblique",
                textAlign: "justify",
              }}
            >
              <strong>
                Nuestra metodología se basa en tres pilares fundamentales:
              </strong>{" "}
              el trabajo en equipo, la mejora continua y la medición de servicios. Estos principios nos permiten colaborar eficazmente con tu organización para garantizar el cumplimiento de la normativa oficial mexicana en el ámbito de la salud ocupacional.
            </MKTypography>
          </MKBox>
        </Grid>
        <Grid item xs={12} md={5} container alignItems="center" justify="center">
          <MKBox>
            <MKBox
              component="img"
              src={macbook}
              alt="SMELS"
              width="95%"
            />
          </MKBox>
        </Grid>
      </Grid>
    </Card>
  </Grid>
</Grid>

      </Container>{" "}
    </MKBox>
  );
}

export default Newsletter;
