// @mui material components
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Divider from '@mui/material/Divider';

// Material Kit 2 React components
import MKBox from "components/MKBox";
import { Typography } from "@mui/material";

// Material Kit 2 React examples
import RotatingCard from "examples/Cards/RotatingCard";
import RotatingCardFront from "examples/Cards/RotatingCard/RotatingCardFront";
import RotatingCardBack from "examples/Cards/RotatingCard/RotatingCardBack";
import DefaultInfoCard from "examples/Cards/InfoCards/DefaultInfoCard";

// Images
import bgFront from "assets/images/laboral_3.jpg";
import bgBack from "assets/images/laboral_2.jpg";

function Information() {
  const blueColor = '#70C7FF'; // Color azul

  return (
    <MKBox component="section" py={6} my={1}>
      <Container>
        <Grid container item xs={11} spacing={1} mb={8} alignItems="center" sx={{ mx: "auto" }}>
          <Grid item xs={12} lg="12" sx={{ mx: "auto", my: 5 }}>
            <RotatingCard>
              <RotatingCardFront
                image={bgFront}
                icon="health_and_safety"
                title={<Typography variant="h4" color="#ffffff" component="p">
                ¡Deja todo en nuestras manos!{" "}
              </Typography>}
                description="Somos una empresa comprometida con la saluda y el bienestar de las empresas y sus trabajadores"
              />
              <RotatingCardBack
                image={bgBack}
                title={<Typography variant="h4" color="#ffffff" component="p">
                ¡Conoce más sobre nuestros servicios!{" "}
              </Typography>}                
                description="Descubre los servicios y facilidades que tenemos para ofrecerte"
                action={{
                  type: "internal",
                  route: "/servicios",
                  label: "Conoce Nuestros Servicios",
                }}
              />
            </RotatingCard>
          </Grid>
          <Grid item xs={12} lg={6} sx={{ ml: "auto" }}>
            <Grid container spacing={1}>
              <Grid item xs={12} md={12}>
                <DefaultInfoCard
                  icon="access_time"
                  title="Ahorro de tiempo"
                  description="InvernaTec te permite monitorear tu invernadero de manera remota, sin tiempos de espera y con solo presionar un boton."
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={12} lg={6} sx={{ ml: "auto" }}>
            <Grid container spacing={1}>
              <Grid item xs={12} md={12}>
                <DefaultInfoCard
                  icon="access_time"
                  title="Ahorro de tiempo"
                  description="InvernaTec te permite monitorear tu invernadero de manera remota, sin tiempos de espera y con solo presionar un boton."
                />
              </Grid>
            </Grid>
          </Grid>
          <Grid item xs={12} lg={6} sx={{ ml: "auto" }}>
            <Grid container spacing={1}>
              <Grid item xs={12} md={12}>
                <DefaultInfoCard
                  icon="access_time"
                  title="Ahorro de tiempo"
                  description="InvernaTec te permite monitorear tu invernadero de manera remota, sin tiempos de espera y con solo presionar un boton."
                />
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Container>
      <Divider style={{ backgroundColor: blueColor, height: 5, marginTop: 200 }} />

    </MKBox>
  );
}

export default Information;
