/*
 */

// @mui material components
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";

// Material Kit 2 React components
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";

// Material Kit 2 React examples
import DefaultInfoCard from "examples/Cards/InfoCards/DefaultInfoCard";
import { useState } from "react";
import {
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Paper,
  Typography,
  Divider,
  Card
} from "@mui/material";
import InboxIcon from "@mui/icons-material/Inbox";
import MailIcon from "@mui/icons-material/Mail";
import DraftsIcon from "@mui/icons-material/Drafts";
import bgImage from "assets/images/v870-tang-35.jpg";

const menuItems = [
  {
    icon: <InboxIcon />,
    text: "Atención médica primaria",
    description: <MKTypography>Conocimiento mutuo sobre el tema</MKTypography>,
  },
  {
    icon: <MailIcon />,
    text: "Llenado de ST7 ",
    description: "Descripción del Mail",
  },
  {
    icon: <DraftsIcon />,
    text: "Atención de Accidentes laborales leves",
    description: "Descripción del Drafts",
  },
  {
    icon: <DraftsIcon />,
    text: "Exámenes Médicos",
    description: "Descripción del Drafts",
  },
  {
    icon: <DraftsIcon />,
    text: "Campañas de salud ",
    description: "Descripción del Drafts",
  },
  {
    icon: <DraftsIcon />,
    text: "Campañas y platicas  de Seguridad",
    description: "Descripción del Drafts",
  },
];

const blueColor = "f0f0f0"; // Color azul

function Information() {
  const [selectedItem, setSelectedItem] = useState(menuItems[0]);

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };
  return (
    <MKBox component="section" pb={12}>
        <Grid container spacing={5} alignItems="center">
          <Grid item sm={12} lg={12} mb={12}>
          <Container>
          <Grid container justifyContent="flex-start">
              <Grid item xs={12} md={4} my={3}>
                <MKBox mb={5}>
                  <DefaultInfoCard
                    icon="inventory"
                    title="Servicios Médicos Profesionales"
                    description="Servicio bajo coordinación de un médico con conocimiento y experiencia en salud laboral."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox mb={5}>
                  <DefaultInfoCard
                    icon="medical_services"
                    title="Salud En Tu Empresa"
                    description="Programa de salud en tu empresa estandarizado."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="badge"
                    title="Manejo y Contrato De Médicos Calificados"
                    description="Servicio de contratación y administración del médico proporcionado a tu empresa, incluye pago, prestaciones y su manejo administrativo."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="payments"
                    title="Deducción de impuestos"
                    description="Servicio deducible de impuestos. "
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="volunteer_activism"
                    title="Promoción de la Salud y Campañas"
                    description="Campañas de salud y promoción de los habitos de salud y seguridad por parte del médico en labores dentro de tu empresa."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="monitoring"
                    title="Estadísticas de Salud Laboral"
                    description="Contamos con servicio de estadísticas de salud laboral."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="monitoring"
                    title="Exámenes Médicos"
                    description="Le ofrecemos exámenes médicos de ingreso, exámenes médicos periódicos, consulta de primer nivel de atención."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="monitoring"
                    title="Implementos Médicos y Farmacia"
                    description="Por un costo extra, le ofrecemos el servicio de implementos médicos y farmacia para su empresa."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="vaccines"
                    title="Implementos Médicos y Farmacia"
                    description="Por un costo extra, le ofrecemos el servicio de implementos médicos y farmacia para su empresa."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="emergency"
                    title="Atención Inmediata"
                    description="Urgencias leves para atención de accidentes de trabajo."
                  />
                </MKBox>{" "}
              </Grid>{" "}
              <Grid item xs={12} md={4} my={3}>
                <MKBox
                  mb={{
                    xs: 5,
                    md: 0,
                  }}
                >
                  <DefaultInfoCard
                    icon="error_med"
                    title="Atención Preferencial Con Especialistas"
                    description="Canalización con especialistas privados para una atención preferencial, enfocada y para disminuir un primer riesgo."
                  />
                </MKBox>{" "}
              </Grid>{" "}
            </Grid>{" "}
          </Container>
            <Divider style={{ backgroundColor: blueColor, height: 5, marginTop: 100 }} />
          </Grid>{" "}
          <Grid
            item
            xs={12}
            sx={{
              mb: 10,
            }}
          >
            <Grid mb={12}>
            <MKTypography
              variant="h2"
              color="info"
              sx={{
                textAlign: "center",
                mb: 3,
              }}
            >
              Listado de servicios{" "}
            </MKTypography>{" "}
            <MKTypography
              variant="body1"
              color="dark"
              opacity={0.8}
              sx={{
                textAlign: "center",
              }}
            >
              Conoce nuestra lista de servicios que tenemos para ofrecerte.{" "}
            </MKTypography>{" "}
            </Grid>
            <MKBox
            minHeight="45rem"
            opacity={0.8}
            width="100%"
            sx={{
              backgroundImage: `url(${bgImage})`,
              backgroundSize: "cover",
              backgroundPosition: "cover",
              backgroundAttachment: "fixed", // Fondo estático
              display: "grid",
              placeItems: "center",
            }}
          >
            <Container>
            <Grid>
                <Grid item md={12} xs={12} >
                  <Card variant="outlined">
                    <Grid
                      container
                      spacing={5}
                    >
                      <Grid item xs={12} md={7}>
                        <MKBox container>
                        <Grid item xs={12} lg={12} mt={12}>
                          <MKBox
                            style={{
                              display: "flex",
                              alignItems: "center",

                            }}

                          >
                            <div
                              style={{
                                flex: 6,
                              }}
                            >
                              <Paper
                                elevation={1}
                                style={{
                                  height: "100%",
                                  overflow: "auto",
                                  marginBottom: "5rem"
                                }}

                              >
                                <List container>
                                  {" "}
                                  {menuItems.map((item) => (
                                    <ListItem
                                      button
                                      style={{marginBottom: "0.5rem", padding: "1rem"}}
                                      selected={item === selectedItem}
                                      onClick={() => handleItemClick(item)}
                                    >
                                      <ListItemIcon> {item.icon} </ListItemIcon>{" "}
                                      <ListItemText primary={item.text} />{" "}
                                    </ListItem>
                                  ))}{" "}
                                </List>{" "}
                              </Paper>{" "}
                            </div>{" "}
                            <MKBox
                              style={{
                                flex: 3,
                                padding: "2vmax",
                                marginBottom: "2rem"
                              }}
                            > 
                              <Typography variant="h5"> {selectedItem.text} </Typography>{" "}
                              <Typography> {selectedItem.description} </Typography>{" "}
                            </MKBox>{" "}
                          </MKBox>{" "}
                        </Grid>{" "}
                        </MKBox>{" "}
                      </Grid>{" "}
                    </Grid>{" "}
                  </Card>{" "}
                </Grid>{" "}
              </Grid> 
            </Container>

          </MKBox>{" "}
          </Grid>{" "}
        </Grid>{" "}
      {/* <Divider
        style={{
          backgroundColor: blueColor,
          height: 5,
          marginTop: 50,
          marginBottom: 100,
        }}
      /> */}
    </MKBox>
  );
}

export default Information;
