import React from "react";
import Button from '@mui/material/Button';
import MKTypography from "components/MKTypography";

function WhatsAppButton() {
  const handleWhatsAppClick = () => {
    // Reemplaza "123456789" con el número de teléfono de WhatsApp al que deseas redirigir.
    const phoneNumber = "6182385321";
    
    // Reemplaza "Hola%20Mundo" con el mensaje que desees enviar en formato URL.
    const message = "Hola,%20necesito%20mas%20informacion";

    // Crea la URL de WhatsApp con el número y el mensaje (opcional).
    const whatsappURL = `https://wa.me/${phoneNumber}?text=${message}`;

    // Redirige a WhatsApp cuando se hace clic en el botón.
    window.location.href = whatsappURL;
  };

  return (
    <>
    <Button variant="contained" style={{backgroundColor: "green", color: "white"}} onClick={handleWhatsAppClick}>
    <MKTypography variant="h6" color="white">    
        <i className="fa fa-whatsapp" aria-hidden="true" />
    </MKTypography>
    <MKTypography color="white" variant="h6" ml={1}> Enviar un mensaje a WhatsApp</MKTypography>
    </Button>
    </>

  );
}

export default WhatsAppButton;
