                  <MKBox component="img" src={vodafone} alt="vodafone" width="100%" opacity={0.7} />
                </Grid>
              </Grid> */}{" "}
        <MKBox mt={7} >
          <MKBox
            variant="gradient"
            bgColor="info"
            position="relative"
            borderRadius="xl"
            sx={{
              overflow: "hidden",
            }}
          >
            <MKBox
              component="img"
              src={bgImage}
              alt="pattern-lines"
              sx={{        backgroundAttachment: "fixed", // Fondo est√°tico
            }}
              position="absolute"
              top={0}
